package Blind75.SlidingWindow;

import java.util.*;

public class LongestRepeatingCharacterReplacement {
    public int characterReplacement(String s, int k) {
        
        Set<Character> set = new HashSet<>();
        for (int i=0; i<s.length(); i++)
        {
            set.add(s.charAt(i));
        }        
        StringBuilder sc = new StringBuilder();
        int max = 0 ;
        int limit =k;
        
        for(char ch : set)
        {   
            int l=0, r = 0;
            sc.setLength(0);
            k=limit;            
            while(r<s.length())
            {

                if(ch==s.charAt(r)&& k>=0 )
                {
                    sc.append(s.charAt(r));
                    max = Math.max(max, sc.length());
                    r++;
                    continue;
                }
                if(ch!=s.charAt(r) && k>0)
                {   
                    sc.append(ch);
                    r++;
                    k--;
                    max =Math.max(max, sc.length());
                    continue;
                }
                if(ch!=s.charAt(r) && k==0)
                {
                    max =Math.max(max, sc.length());
                    sc.setLength(0);
                    l++;
                    r=l;
                    k= limit;
                }
            }
        } 
        return max;
    }
    public static void main(String[] args) {
        LongestRepeatingCharacterReplacement ll = new LongestRepeatingCharacterReplacement();
        String s = "";
        System.out.println(ll.characterReplacement(s, 7520));
    }
}
